import { useState, useEffect } from "react";
import "bootstrap/dist/css/bootstrap.min.css"

type resultProps = {
    name: string, 
    height: number, 
    mass: string, 
    hair_color:string, 
    skin_color: string, 
    eye_color: string, 
    birth_year: string, 
    gender: string, 
    homeworld: string, 
};

 const StarApi=()=> {
  const [result, setResult] = useState<resultProps[]>([]);

  useEffect(() => {
    const fectchApi = async () => {
      const data = await fetch("https://swapi.dev/api/people/", {
        method: "GET"
      });
      const jsonData = await data.json();
      setResult(jsonData.results);
    };

    fectchApi();
  }, []);

  return (
    <div className="App">
    <div className="container">

          <div className="row">

              { result.map((record, i)=> (
                  <div style={{ padding:"10px",backgroundColor:"orange",marginRight:"5px"}}className="col-lg-3 col-md-3 col-sm-3 col-12 mb-4 position-relative text-dark">
                    <h2 >Name: {record.name}</h2>
                    <h2 >Hair Color: {record.hair_color}</h2>
                    <h2 >Gender: {record.gender}</h2>
                  </div>) ) }
          </div>
        </div>

    </div>
    );
}

export default StarApi;
